   company             VARCHAR(29)
  ,location            VARCHAR(16)
  ,industry            VARCHAR(14)
  ,total_laid_off      INTEGER 
  ,percentage_laid_off VARCHAR(20)
  ,date                VARCHAR(10)
  ,stage               VARCHAR(14)
  ,country             VARCHAR(20)
  ,funds_raised        VARCHAR(10)
);
